mapper.update:
UPDATE member_request
SET status = #{status},
    handlername = #{handlername},
    handle_time = #{handleTime}
WHERE id = #{id}
  AND status = 0

mapper.select:
SELECT status
FROM member_request
WHERE id = #{id}

SELECT to_group
FROM member_request
WHERE id = #{id}

SELECT from_user_code
FROM member_request
WHERE id = #{id}
